<?php include_once('BaseQuestion.php');class Question5 extends BaseQuestion {private $code = '';private $desc = '';public function getData() {global $docRoot;return $docRoot . 'q5.php';}public function getInstructions() {return include_once('questions/Q5.php');}public function getHttpResponse($get, $post) {if (strtoupper($_SERVER['REQUEST_METHOD'] === 'POST') || strtoupper($_SERVER['REQUEST_METHOD']) === 'GET') {$action = ((!empty($get) && !empty($get['action'])) ? $get['action'] : ((!empty($post) && !empty($post['action'])) ? $post['action'] : null));if (!empty($action) && (strtolower($action) == 'get' || strtolower($action) == 'post')) {$this->generateCode();$this->generateDesc();return $this->code . ',' . $this->desc;} else {return 'Unknown Action';}} else {return 'Unknown Request Method';}}private function generateCode() {$cd = '';$ln = (3 + mt_rand(1,3));for ($i = 0; $i < $ln; $i++) {$cd .= mt_rand(1,10);}if (((bool) mt_rand(0,1)) && ((bool) mt_rand(0,1)) && ((bool) mt_rand(0,1))) {$cd .= mt_rand(1,9);}if (((bool) mt_rand(0,1)) && ((bool) mt_rand(0,1))) {$characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';$charactersLength = strlen($characters);$ln2 = mt_rand(1,5);for ($i = 0; $i < $ln2; $i++) {$cd .= $characters[mt_rand(0, $charactersLength - 1)];if ((bool) mt_rand(0,1)) {$cd .= $characters[mt_rand(0, $charactersLength - 1)];} else {$cd = $characters[mt_rand(0, $charactersLength - 1)] . $cd;}}}$this->code = $cd;}private function generateDesc() {$desc = array("Approved", "Pending", "Declined", "Abandoned", "Error", "Success", "Unknown", "Halted");$this->desc = $desc[mt_rand(0, (count($desc) - 1))];}}