<?php $docRoot = '';$sep = '/';$server = $_SERVER['SERVER_NAME'];$path = $_SERVER['PHP_SELF'];$http = ((isset($_SERVER['HTTPS']) && $_SERVER['HTTPS'] == 'on' ? 'https://' : 'http://'));$project = 'paygate-test-php';$pathArray = explode($sep, $path);$newPath = '';foreach ($pathArray as $str) {if (!empty($str)) {if ($str == $project) {break;} else {$newPath .= $sep.$str;}}}$docRoot = $http . $server .  $newPath . $sep . $project . $sep;$GLOBALS['docRoot'] = $docRoot;